import React, { useState, useEffect } from 'react';

// Типы данных
interface Param {
  id: number;
  name: string;
  type: 'string';
}

interface ParamValue {
  paramId: number;
  value: string;
}

// interface Color {
//   r: number;
//   g: number;
//   b: number;
//   a?: number;
// }

interface Model {
  paramValues: ParamValue[];
  colors: Color[];
}

interface Props {
  params: Param[];
  model: Model;
}

interface State {
  paramValues: ParamValue[];
}

class ParamEditor extends React.Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = {
      paramValues: props.model.paramValues,
    };
  }

  handleParamValueChange = (paramId: number, value: string) => {
    const { paramValues } = this.state;
    const index = paramValues.findIndex((pv) => pv.paramId === paramId);

    if (index !== -1) {
      this.setState((prevState) => ({
        paramValues: prevState.paramValues.map((pv) =>
          pv.paramId === paramId ? { ...pv, value } : pv
        ),
      }));
    }
  };

  getModel = (): Model => ({
    paramValues: this.state.paramValues,
    colors: this.props.model.colors,
  });

  render() {
    return (
      <div>
        {this.props.params.map((param) => (
          <div key={param.id}>
            <label htmlFor={`param-${param.id}`}>{param.name}:</label>
            <input
              id={`param-${param.id}`}
              type="text"
              value={
                this.state.paramValues.find((pv) => pv.paramId === param.id)
                  ?.value || ''
              }
              onChange={(e) => this.handleParamValueChange(param.id, e.target.value)}
            />
          </div>
        ))}
      </div>
    );
  }
}

const App = () => {
  const [params, setParams] = useState<Param[]>([]);
  const [model, setModel] = useState<Model>({ paramValues: [], colors: [] });

  useEffect(() => {
    const fetchData = async () => {
      const paramsResponse = await fetch('/params.json');
      const paramsData = await paramsResponse.json();
      setParams(paramsData);

      const modelResponse = await fetch('/model.json');
      const modelData = await modelResponse.json();
      setModel(modelData);
    };
    fetchData();
  }, []);

  const handleModelChange = (newModel: Model) => {
    setModel(newModel);
  };

  return (
    <div>
      <ParamEditor params={params} model={model} />
      <button onClick={() => handleModelChange(model)}>Get Model</button>
    </div>
  );
};

export default App;
