function ToDo({ todo, toggleTask, removeTask }) {
	const { name, content, complete, createdAt, updatedAt } = todo;
	return (
		<div key={todo.id} className="item-todo">
			<div
				className={complete ? "item-text strike" : "item-text"}
				onClick={() => toggleTask(todo.id)}
			>
				{name}
			</div>
			<div className="item-delete" onClick={() => removeTask(todo.id)}>
				<svg
					width="20.000000"
					height="19.000000"
					viewBox="0 0 20 19"
					fill="none"
				>
					<defs />
					<path
						id="Vector"
						d="M11.76 9L19.63 1.13C20.12 0.64 20.12 -0.15 19.63 -0.64C19.14 -1.13 18.35 -1.13 17.86 -0.64L10 7.23L2.13 -0.64C1.64 -1.13 0.85 -1.13 0.36 -0.64C-0.13 -0.15 -0.13 0.64 0.36 1.13L8.23 9L0.36 16.86C-0.13 17.35 -0.13 18.14 0.36 18.63C0.61 18.87 0.93 18.99 1.25 18.99C1.57 18.99 1.89 18.87 2.13 18.63L10 10.76L17.86 18.63C18.1 18.87 18.42 18.99 18.74 18.99C19.06 18.99 19.38 18.87 19.63 18.63C20.12 18.14 20.12 17.35 19.63 16.86L11.76 9Z"
						fill="#52565B"
						fill-opacity="1.000000"
						fill-rule="nonzero"
					/>
				</svg>
			</div>
			<div className="item-dates">
				<div className="item-date">{content}</div>
				<div className="item-date">Дата создания: {formatDate(createdAt)}</div>
				<div className="item-date">Дата изменения: {formatDate(updatedAt)}</div>
			</div>
		</div>
	);
}

const formatDate = (dateString) => {
	const date = new Date(dateString);
	const day = date.getDate().toString().padStart(2, "0");
	const month = (date.getMonth() + 1).toString().padStart(2, "0");
	const year = date.getFullYear();
	const hours = date.getHours().toString().padStart(2, "0");
	const minutes = date.getMinutes().toString().padStart(2, "0");
	return `${day}.${month}.${year}:${hours}:${minutes}`;
};

export default ToDo;
