function ToDo({ todo, toggleTask, removeTask }) {
    const { name, content, complete, createdAt, updatedAt } = todo;
    return (
      <div key={todo.id} className="item-todo">
        <div
          className={complete ? "item-text strike" : "item-text"}
          onClick={() => toggleTask(todo.id)}
        >
          {name}
        </div>
        <div className="item-delete" onClick={() => removeTask(todo.id)}>
          <svg width="10.000000" height="10.000000" viewBox="0 0 7 7" fill="none">
            <path id="Vector" d="M4.11 3L6.87 0.24C7.04 0.07 7.04 -0.21 6.87 -0.38C6.7 -0.55 6.42 -0.55 6.25 -0.38L3.5 2.38L0.74 -0.38C0.57 -0.55 0.29 -0.55 0.12 -0.38C-0.05 -0.21 -0.05 0.07 0.12 0.24L2.88 3L0.12 5.75C-0.05 5.92 -0.05 6.2 0.12 6.37C0.21 6.45 0.32 6.49 0.43 6.49C0.54 6.49 0.66 6.45 0.74 6.37C0.83 6.29 0.92 6.18 0.99 6.09L3.5 2.63L6.01 6.09C6.08 5.99 6.17 5.88 6.25 5.8C6.32 5.72 6.41 5.61 6.49 5.53C6.57 5.45 6.66 5.34 6.73 5.26L3.5 1.81L6.73 5.26C6.81 5.18 6.9 5.07 6.98 4.99C7.06 4.91 7.15 4.79 7.22 4.71C7.3 4.63 7.39 4.52 7.47 4.44C7.54 4.36 7.63 4.25 7.7 4.17C7.78 4.09 7.87 3.97 7.94 3.89L4.11 3Z" fill="#52565B" fill-opacity="1.000000" fill-rule="evenodd"/>
          </svg>
        </div>
        <div className="item-dates">
          <div className="item-date">{content}</div>
          <div className="item-date">Дата создания: {formatDate(createdAt)}</div>
          <div className="item-date">Дата изменения: {formatDate(updatedAt)}</div>
        </div>
      </div>
    );
  }



  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const day = date.getDate().toString().padStart(2, "0");
    const month = (date.getMonth() + 1).toString().padStart(2, "0");
    const year = date.getFullYear();
    const hours = date.getHours().toString().padStart(2, "0");
    const minutes = date.getMinutes().toString().padStart(2, "0");
    return `${day}.${month}.${year}:${hours}:${minutes}`;
  };  

export default ToDo
