import React from 'react';
import { App } from './App.1';

// Типы данных
interface Param {
  id: number;
  name: string;
  type: 'string';
}

interface ParamValue {
  paramId: number;
  value: string;
}

interface Color {
  r: number;
  g: number;
  b: number;
  a?: number;
}

export interface Model {
  paramValues: ParamValue[];
  colors: Color[];
}

interface Props {
  params: Param[];
  model: Model;
}

interface State {
  paramValues: ParamValue[];
}

export class ParamEditor extends React.Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = {
      paramValues: props.model.paramValues,
    };
  }

  handleParamValueChange = (paramId: number, value: string) => {
    const { paramValues } = this.state;
    const index = paramValues.findIndex((pv) => pv.paramId === paramId);

    if (index !== -1) {
      this.setState((prevState) => ({
        paramValues: prevState.paramValues.map((pv) =>
          pv.paramId === paramId ? { ...pv, value } : pv
        ),
      }));
    }
  };

  getModel = (): Model => ({
    paramValues: this.state.paramValues,
    colors: this.props.model.colors,
  });

  render() {
    return (
      <div>
        {this.props.params.map((param) => (
          <div key={param.id}>
            <label htmlFor={`param-${param.id}`}>{param.name}:</label>
            <input
              id={`param-${param.id}`}
              type="text"
              value={
                this.state.paramValues.find((pv) => pv.paramId === param.id)
                  ?.value || ''
              }
              onChange={(e) => this.handleParamValueChange(param.id, e.target.value)}
            />
          </div>
        ))}
      </div>
    );
  }
}

// Пример использования
export const params: Param[] = [
  {
    id: 1,
    name: 'Назначение',
    type: 'string',
  },
  {
    id: 2,
    name: 'Длина',
    type: 'string',
  },
];

export const model: Model = {
  paramValues: [
    {
      paramId: 1,
      value: 'повседневное',
    },
    {
      paramId: 2,
      value: 'макси',
    },
  ],
  colors: [],
};

export default App;
